import React, { useState } from 'react'
import Conditions from '/components/sonditions/Conditions'

const Forecast = () => {
    let [responseObj, setResponseObj] = useState({})

    function getForecast() {
       fetch('https://community-open-weather-map.p.rapidapi.com/weather?callback=test&id=2172797&units=%2522metric%2522%20or%20%2522imperial%2522&mode=xml%252C%20html&q=Oslo%252C%20Norway')
       .then(response => response.json())
       .then(response => {
           setResponseObj(response)
       })
    }

   return (
    <div>
        <h2>Find Current Weather Conditions</h2>
    <div>
        {JSON.stringify(responseObj)}
    </div>
    <button onClick={getForecast}>Get Forecast</button>
</div>
   )
}
export default Forecast;
